// Quiz.tsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Question from './QuizQuestions';
import '../Quiz.css';


const Quiz: React.FC = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [questions, setQuestions] = useState([]);

  // Fetch questions from the API and set them in the state
  useEffect(() => {
    async function fetchQuestions() {
      try {
        const response = await axios.get(
          'https://scs-interview-api.herokuapp.com/questions'
        );
        console.log(response.data)
        setQuestions(response.data);
      } catch (error) {
        console.error('Error fetching questions:', error);
      }
    }

    fetchQuestions();
  }, []);

  // Handle the user's answer to a question
  const handleAnswer = (isCorrect: boolean) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    else

    // Check if there are more questions
    if (currentQuestionIndex + 1 < questions.length) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowResult(true);
    }
  };

  // Function to move to the next question
  const onNextQuestion = () => {
    if (currentQuestionIndex + 1 < questions.length) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowResult(true);
    }
  };

  return (
    <div className="Quiz">
      {showResult ? (
        <div>
          <h2>Quiz Completed!</h2>
          <p>You answered {score} out of {questions.length} questions correctly.</p>
        </div>
      ) : questions.length > 0 && currentQuestionIndex < questions.length ?  (
        <Question
          questionData={questions[currentQuestionIndex]}
          onAnswer={handleAnswer}
          onNextQuestion={onNextQuestion} 
        />
      ) : (
        <p>Loading questions...</p>
      )}
    </div>
  );
};

export default Quiz;
