// Question.tsx
import React, { useState, useEffect } from 'react';
import '../Quiz.css';

interface QuestionProps {
  questionData: {
    imageUrl: string;
    question: string;
    options: string[];
    answer: number;
    time: number;
  };
  onAnswer: (isCorrect: boolean) => void;
  onNextQuestion: () => void;
}

const Question: React.FC<QuestionProps> = ({
  questionData,
  onAnswer,
  onNextQuestion,
}) => {
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const [timer, setTimer] = useState<number>(questionData.time);
  const [showResult, setShowResult] = useState<boolean>(false);

  useEffect(() => {
    setSelectedOption(null);
    setTimer(questionData.time);
    setShowResult(false);

    const interval = setInterval(() => {
      setTimer((prevTimer) => prevTimer - 1);
    }, 1000);

    return () => clearInterval(interval);
  }, [questionData]);

  useEffect(() => {
    if (timer === 0 && selectedOption === null) {
      // If no option is selected when the timer runs out
      setShowResult(true);
      onAnswer(false); // Mark the question as incorrect
      const resultTimeout = setTimeout(() => {
        onNextQuestion();
        clearTimeout(resultTimeout);
      }, 5000); // Delay for 5 seconds before moving to the next question
    }
  }, [timer, selectedOption, onAnswer, onNextQuestion]);

  const handleOptionClick = (index: number) => {
    if (timer > 0 && !showResult) {
      setSelectedOption(index);

      if (index === questionData.answer) {
        // If the correct answer is selected
        setShowResult(true);
        onAnswer(true); // Mark the question as correct
        const resultTimeout = setTimeout(() => {
          onNextQuestion();
          clearTimeout(resultTimeout);
        }, 5000); // Delay for 5 seconds before moving to the next question
      } else {
        // If an incorrect answer is selected
        setShowResult(true);
        onAnswer(false); // Mark the question as incorrect
        const resultTimeout = setTimeout(() => {
          onNextQuestion();
          clearTimeout(resultTimeout);
        }, 5000); // Delay for 5 seconds before moving to the next question
      }
    }
  };

  return (
    <div className="Question">
      <div className="ImageContainer">
        <img src={questionData.imageUrl} alt="Question" className="Image" />
      </div>
      <h2>{questionData.question}</h2>
      <div className="Options">
        {questionData.options.map((option, index) => (
          <button
            key={index}
            className={`Option ${selectedOption === index ? 'Selected' : ''} ${
              showResult && index === questionData.answer
                ? 'Correct'
                : showResult && selectedOption === index
                ? 'Incorrect'
                : ''
            }`}
            onClick={() => handleOptionClick(index)}
            disabled={showResult}
          >
            {option}
          </button>
        ))}
      </div>
      <p>Time Remaining: {timer} seconds</p>
    </div>
  );
};

export default Question;
