// Question.tsx
import React, { useState, useEffect } from 'react';
import '../Quiz.css'; // Import the CSS file

interface QuestionProps {
  questionData: {
    imageUrl: string;
    question: string;
    options: string[];
    answer: number;
    time: number;
  };
  onAnswer: (isCorrect: boolean) => void;
}

const Question: React.FC<QuestionProps> = ({ questionData, onAnswer }) => {
  const [selectedAnswer, setSelectedAnswer] = useState<number | null>(null);
  const [timer, setTimer] = useState<number>(questionData.time);

  useEffect(() => {
    setSelectedAnswer(null); // Reset selectedAnswer when a new question is received
    setTimer(questionData.time); // Reset the timer when a new question is received
  }, [questionData]);

  useEffect(() => {
    if (timer > 0) {
      const interval = setInterval(() => {
        setTimer(timer - 1);
      }, 1000);

      return () => clearInterval(interval);
    } else {
      handleAnswer();
    }
  }, [timer]);

  const handleAnswer = () => {
    if (selectedAnswer !== null) {
      const isCorrect = selectedAnswer === questionData.answer;
      onAnswer(isCorrect);
    }
  };

  return (
    <div className="Question">
            <div className="ImageContainer"> {/* Image container with fixed height */}
        <img src={questionData.imageUrl} alt="Question" className="Image" />
      </div>
      <h2>{questionData.question}</h2> 
      <ul>
        {questionData.options.map((option, index) => (
          <li key={index}>
            <label>
              <input
                type="radio"
                name="answer"
                value={index}
                checked={selectedAnswer === index}
                onChange={() => setSelectedAnswer(index)}
                disabled={timer <= 0}
              />
              {option}
            </label>
          </li>
        ))}
      </ul>
      <p>Time Remaining: {timer} seconds</p>
    </div>
  );
};

export default Question;
