// Question.tsx
import React, { useState, useEffect } from 'react';
import '../Quiz.css';

interface QuestionProps {
  questionData: {
    imageUrl: string;
    question: string;
    options: string[];
    answer: number;
    time: number;
  };
  onAnswer: (isCorrect: boolean) => void;
  onNextQuestion: () => void;
}

const Question: React.FC<QuestionProps> = ({
  questionData,
  onAnswer,
  onNextQuestion,
}) => {
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const [timer, setTimer] = useState<number>(questionData.time);
  const [correctOption, setCorrectOption] = useState<number | null>(null);

  useEffect(() => {
    setSelectedOption(null);
    setTimer(questionData.time);
    setCorrectOption(null);
  }, [questionData]);

  useEffect(() => {
    let delay: NodeJS.Timeout | null = null;

    if (timer > 0) {
      const interval = setInterval(() => {
        setTimer(timer - 1);
      }, 1000);

      return () => {
        clearInterval(interval);
        if (delay) clearTimeout(delay);
      };
    } else {
      if (selectedOption !== null) {
        // Handle the answer (correct or incorrect)
        const isCorrect = selectedOption === questionData.answer;
        onAnswer(isCorrect);
        delay = setTimeout(() => {
          onNextQuestion();
        }, 5000); // Delay for 5 seconds
      } else {
        // If the timer runs out and no option was selected, mark it as incorrect
        onAnswer(false);
        delay = setTimeout(() => {
          onNextQuestion();
        }, 5000); // Delay for 5 seconds
      }

      // Set the correct answer background color to green
      setCorrectOption(questionData.answer);
    }
  }, [timer]);

  const optionClass = (index: number) => {
    if (correctOption !== null) {
      if (index === selectedOption) {
        return correctOption === index ? 'Correct' : 'Incorrect';
      }
      return correctOption === index ? 'Correct' : '';
    }
    return selectedOption === index ? 'Selected' : '';
  };

  return (
    <div className="Question">
      <div className="ImageContainer">
        <img src={questionData.imageUrl} alt="Question" className="Image" />
      </div>
      <h2>{questionData.question}</h2>
      <div className="Options">
        {questionData.options.map((option, index) => (
          <button
            key={index}
            className={`Option ${optionClass(index)}`}
            onClick={() => setSelectedOption(index)}
            disabled={timer <= 0}
          >
            {option}
          </button>
        ))}
      </div>
      <p>Time Remaining: {timer} seconds</p>
    </div>
  );
};

export default Question;
