// Question.tsx
import React, { useState } from 'react';

interface QuizQuestionProps {
  QuizQuestionData: {
    question: string;
    options: string[];
    correctAnswer: string;
  };
  checkAnswer: (isCorrect: boolean) => void;
}

const QuizQuestions: React.FC<QuizQuestionProps> = ({ QuizQuestionData, checkAnswer }) => {
  const [selectedAnswer, setSelectedAnswer] = useState<string | null>(null);

  const handleAnswer = () => {
    const isCorrect = selectedAnswer === QuizQuestionData.correctAnswer;
    checkAnswer(isCorrect);
  };

  return (
    <div>
      <h2>{QuizQuestionData.question}</h2>
      <ul>
        {QuizQuestionData.options.map((option, index) => (
          <li key={index}>
            <label>
              <input
                type="radio"
                name="answer"
                value={option}
                checked={selectedAnswer === option}
                onChange={() => setSelectedAnswer(option)}
              />
              {option}
            </label>
          </li>
        ))}
      </ul>
      <button onClick={handleAnswer}>Submit Answer</button>
    </div>
  );
};

export default QuizQuestions;
