import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Question from './QuizQuestions'; // Make sure to import Question correctly
import '../Quiz.css';

const Quiz: React.FC = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    async function fetchQuestions() {
      try {
        const response = await axios.get(
          'https://scs-interview-api.herokuapp.com/questions'
        );
        setQuestions(response.data);
      } catch (error) {
        console.error('Error fetching questions:', error);
      }
    }

    fetchQuestions();
  }, []);

  const handleAnswer = (isCorrect: boolean) => {
    if (isCorrect) {
      setScore(score + 1);
    }

    if (currentQuestionIndex + 1 < questions.length) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowResult(true);
    }
  };

  const onNextQuestion = () => {
    if (currentQuestionIndex + 1 < questions.length) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowResult(true);
    }
  };

  return (
    <div className="Question">
      <div className="ImageContainer">
        <img src={questionData.imageUrl} alt="Question" className="Image" />
      </div>
      <h2>{questionData.question}</h2>
      <div className="Options">
        {questionData.options.map((option, index) => (
          <button
            key={index}
            className={`Option ${optionClass(index)}`}
            onClick={() => setSelectedOption(index)}
            disabled={timer <= 0}
          >
            {option}
          </button>
        ))}
      </div>
      <div className="TimerContainer">
        <svg className="CircularTimer">
          <circle
            className="CircularTimer-Path"
            cx="25"
            cy="25"
            r="20"
            fill="transparent"
          />
          <circle
            className="CircularTimer-Progress"
            cx="25"
            cy="25"
            r="20"
            fill="transparent"
            strokeDasharray="125.6"
            strokeDashoffset={((questionData.time - timer) / questionData.time) * 125.6}
          />
        </svg>
        <div className="Countdown">{timer}</div>
      </div>
    </div>
  );

};

export default Quiz;
