// Quiz.tsx

import React, { useEffect, useState } from 'react';
import Question from './QuizQuestions';

// Explicitly declare handleData as a global function
declare global {
  interface Window {
    handleData: (data: any) => void;
  }
}

const Quiz: React.FC = () => {
  const [questions, setQuestions] = useState<any[]>([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState<number>(0);
  const [score, setScore] = useState<number>(0);
  const [showResult, setShowResult] = useState<boolean>(false);

  // Callback function to handle JSONP response
  window.handleData = (data) => {
    console.log('Data received:', data);
    // Handle the data here, such as setting the questions state
    setQuestions(data);
  };

  useEffect(() => {
    // Dynamically create a script element to fetch data via JSONP
    const script = document.createElement('script');
    script.src = 'https://scs-interview-api.herokuapp.com/questions?callback=handleData';
    document.body.appendChild(script);

    // Cleanup: remove the script element when the component unmounts
    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const handleAnswer = (isCorrect: boolean) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    setTimeout(() => {
      if (currentQuestionIndex < questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
      } else {
        setShowResult(true);
      }
    }, 1000);
  };

  return (
    <div>

    </div>
  );
};

export default Quiz;
