import React, { useState, useEffect, useRef } from 'react';
import '../Quiz.css';

interface QuestionProps {
  questionData: {
    imageUrl: string;
    question: string;
    options: string[];
    answer: number;
    time: number;
  };
  onAnswer: (isCorrect: boolean) => void;
  onNextQuestion: () => void;
}

const Question: React.FC<QuestionProps> = ({
  questionData,
  onAnswer,
  onNextQuestion,
}) => {
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const [timer, setTimer] = useState<number>(questionData.time);
  const [correctOption, setCorrectOption] = useState<number | null>(null);
  const timerRef = useRef<NodeJS.Timer | null>(null);

  useEffect(() => {
    setSelectedOption(null);
    setTimer(questionData.time);
    setCorrectOption(null);

    // Start the timer countdown
    timerRef.current = setInterval(() => {
      setTimer((prevTimer) => {
        if (prevTimer <= 0) {
          clearInterval(timerRef.current);
        }
        return prevTimer - 1;
      });
    }, 1000);

    return () => {
      // Clean up the timer interval
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [questionData]);

  useEffect(() => {
    if (timer === 0) {
      // When the timer runs out
      clearInterval(timerRef.current);
      if (selectedOption === null) {
        // If no option was selected, mark it as incorrect
        handleAnswer(false);
      } else {
        // If an option was selected, check if it's correct
        handleAnswer(selectedOption === questionData.answer);
      }

      // Set the correct answer background color to green
      setCorrectOption(questionData.answer);

      // Delay for 5 seconds before moving to the next question
      const delay = setTimeout(() => {
        onNextQuestion();
        clearTimeout(delay);
      }, 5000); // Delay for 5 seconds
    }
  }, [timer, selectedOption, questionData]);

  const handleAnswer = (isCorrect: boolean) => {
    if (selectedOption !== null) {
      onAnswer(isCorrect);
    }
  };

  const optionClass = (index: number) => {
    if (correctOption !== null) {
      if (correctOption === index) {
        return 'Correct';
      } else if (selectedOption === index) {
        return 'Incorrect';
      }
    }
    return selectedOption === index ? 'Selected' : '';
  };

  return (
    <div className="Question">
      <div className="ImageContainer">
        <img src={questionData.imageUrl} alt="Question" className="Image" />
      </div>
      <h2>{questionData.question}</h2>
      <div className="Options">
        {questionData.options.map((option, index) => (
          <button
            key={index}
            className={`Option ${optionClass(index)}`}
            onClick={() => setSelectedOption(index)}
            disabled={timer <= 0}
          >
            {option}
          </button>
        ))}
      </div>
      <p>Time Remaining: {timer} seconds</p>
    </div>
  );
};

export default Question;
