import React, { useState, useEffect } from 'react';
import '../Quiz.css';

interface QuestionProps {
  questionData: {
    imageUrl: string;
    question: string;
    options: string[];
    answer: number;
    time: number;
  };
  onAnswer: (isCorrect: boolean) => void;
  onNextQuestion: () => void;
}

const Question: React.FC<QuestionProps> = ({
  questionData,
  onAnswer,
  onNextQuestion,
}) => {
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const [timer, setTimer] = useState<number>(questionData.time);
  const [correctOption, setCorrectOption] = useState<number | null>(null);

  useEffect(() => {
    setSelectedOption(null);
    setTimer(questionData.time);
    setCorrectOption(null);

    const timerInterval = setInterval(() => {
      setTimer((prevTimer) => prevTimer - 1);
    }, 1000);

    // Clear the timer and proceed to the next question when the timer reaches 0
    if (timer === 0) {
      clearInterval(timerInterval);
      handleAnswer(false);
      onNextQuestion();
    }

    return () => clearInterval(timerInterval);
  }, [questionData, onNextQuestion, timer]);

  const handleAnswer = (isCorrect: boolean) => {
    if (selectedOption !== null) {
      onAnswer(isCorrect);

      // Delay for 5 seconds before moving to the next question
      setTimeout(() => {
        onNextQuestion();
      }, 5000);
    }
  };

  const optionClass = (index: number) => {
    if (correctOption !== null) {
      if (correctOption === index) {
        return 'Correct'; // Prioritize correct answer background color
      } else if (correctOption !== index) {
        return 'Incorrect'; // If incorrect option is selected, show it as incorrect
      }
    }
    return selectedOption === index ? 'Selected' : '';
  };

  return (
    <div className="Question">
      <div className="ImageContainer">
        <img src={questionData.imageUrl} alt="Question" className="Image" />
      </div>
      <h2>{questionData.question}</h2>
      <div className="Options">
        {questionData.options.map((option, index) => (
          <button
            key={index}
            className={`Option ${optionClass(index)}`}
            onClick={() => {
              if (selectedOption === null && timer > 0) {
                setSelectedOption(index);
                setCorrectOption(questionData.answer);
                handleAnswer(questionData.answer === index);
              }
            }}
            disabled={selectedOption !== null || timer <= 0}
          >
            {option}
          </button>
        ))}
      </div>
      <p>Time Remaining: {timer} seconds</p>
    </div>
  );
};

export default Question;
