import React, { useState, useEffect } from 'react';
import '../Quiz.css';

interface QuestionProps {
  questionData: {
    imageUrl: string;
    question: string;
    options: string[];
    answer: number;
    time: number;
  };
  onAnswer: (isCorrect: boolean) => void;
  onNextQuestion: () => void;
}

const Question: React.FC<QuestionProps> = ({
  questionData,
  onAnswer,
  onNextQuestion,
}) => {
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const [timer, setTimer] = useState<number>(questionData.time);
  const [isAnswered, setIsAnswered] = useState<boolean>(false);

  useEffect(() => {
    setSelectedOption(null);
    setTimer(questionData.time);
    setIsAnswered(false);
  }, [questionData]);

  useEffect(() => {
    let timerId: NodeJS.Timeout;

    if (timer > 0 && !isAnswered) {
      timerId = setTimeout(() => {
        setTimer(timer - 1);
      }, 1000);
    } else if (!isAnswered) {
      // If no option was selected, mark it as incorrect
      handleAnswer(false);
      setIsAnswered(true);

      // Delay for 5 seconds before moving to the next question
      timerId = setTimeout(() => {
        onNextQuestion();
      }, 5000); // Delay for 5 seconds
    }

    return () => clearTimeout(timerId);
  }, [timer, isAnswered]);

  const handleAnswer = (isCorrect: boolean) => {
    if (!isAnswered) {
      setIsAnswered(true);
      onAnswer(isCorrect);
    }
  };

  const optionClass = (index: number) => {
    if (isAnswered) {
      if (questionData.answer === index) {
        return 'Correct';
      } else if (selectedOption === index) {
        return 'Incorrect';
      }
    }
    return selectedOption === index ? 'Selected' : '';
  };

  return (
    <div className="Question">
      <div className="ImageContainer">
        <img src={questionData.imageUrl} alt="Question" className="Image" />
      </div>
      <h2>{questionData.question}</h2>
      <div className="Options">
        {questionData.options.map((option, index) => (
          <button
            key={index}
            className={`Option ${optionClass(index)}`}
            onClick={() => handleAnswer(index === questionData.answer)}
            disabled={isAnswered}
          >
            {option}
          </button>
        ))}
      </div>
      <p>Time Remaining: {timer} seconds</p>
    </div>
  );
};

export default Question;
