import React, { useState, useEffect } from 'react';
import '../Quiz.css';

interface QuestionProps {
  questionData: {
    imageUrl: string;
    question: string;
    options: string[];
    answer: number;
    time: number;
  };
  onAnswer: (isCorrect: boolean) => void;
  onNextQuestion: () => void;
}

const Question: React.FC<QuestionProps> = ({
  questionData,
  onAnswer,
  onNextQuestion,
}) => {
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const [timer, setTimer] = useState<number>(questionData.time);
  const [correctOption, setCorrectOption] = useState<number | null>(null);

  useEffect(() => {
    setSelectedOption(null);
    setTimer(questionData.time);
    setCorrectOption(null);
  }, [questionData]);

  useEffect(() => {
    if (timer > 0) {
      const interval = setInterval(() => {
        setTimer(timer - 1);
      }, 1000);

      return () => clearInterval(interval);
    } else {
      if (selectedOption === null) {
        // No option was selected before the timer ended
        // Handle as incorrect
        setCorrectOption(questionData.answer);
        handleAnswer(false);
      } else {
        const isCorrect = selectedOption === questionData.answer;
        setCorrectOption(questionData.answer);
        handleAnswer(isCorrect);
      }
    }
  }, [timer, selectedOption]);

  const handleAnswer = (isCorrect: boolean) => {
    if (selectedOption !== null) {
      onAnswer(isCorrect);
      const delay = setTimeout(() => {
        onNextQuestion();
        clearTimeout(delay);
      }, 3000); // Delay for 3 seconds
    }
  };

  return (
    <div className="Question">
      <div className="ImageContainer">
        <img src={questionData.imageUrl} alt="Question" className="Image" />
        <div className="TimerContainer">
          <div className="CircularTimer">
            <svg viewBox="0 0 36 36" className="CircularTimer-Path">
              <path
                d="M18 2.0845
                a 15.9155 15.9155 0 0 1 0 31.831
                a 15.9155 15.9155 0 0 1 0 -31.831"
              />
            </svg>
            <svg
              viewBox="0 0 36 36"
              className="CircularTimer-Progress"
              style={{
                strokeDasharray: `${(
                  (questionData.time - timer) /
                  questionData.time *
                  125
                ).toFixed(2)} 125`,
              }}
            >
              <path
                d="M18 2.0845
                a 15.9155 15.9155 0 0 1 0 31.831
                a 15.9155 15.9155 0 0 1 0 -31.831"
              />
            </svg>
          </div>
          <div className="Countdown">
            Time Remaining: {timer} seconds
          </div>
        </div>
      </div>
      <h2>{questionData.question}</h2>
      <div className="Options">
        {questionData.options.map((option, index) => (
          <button
            key={index}

            onClick={() => setSelectedOption(index)}
            disabled={timer <= 0}
          >
            {option}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Question;
