// Question.tsx
import React, { useState, useEffect } from 'react';

interface QuestionProps {
  questionData: {
    imageUrl: string;
    question: string;
    options: string[];
    answer: number;
    time: number;
  };
  onAnswer: (isCorrect: boolean) => void;
  onNextQuestion: () => void;
}

const Question: React.FC<QuestionProps> = ({
  questionData,
  onAnswer,
  onNextQuestion,
}) => {
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const [timer, setTimer] = useState<number>(questionData.time);
  const [correctOption, setCorrectOption] = useState<number | null>(null);

  useEffect(() => {
    setSelectedOption(null); // Reset selectedOption when a new question is received
    setTimer(questionData.time); // Reset the timer when a new question is received
    setCorrectOption(null); // Reset correctOption
  }, [questionData]);

  useEffect(() => {
    if (timer > 0) {
      const interval = setInterval(() => {
        setTimer(timer - 1);
      }, 1000);

      return () => clearInterval(interval);
    } else {
      if (selectedOption !== null) {
        handleAnswer(false); // Automatically submit an incorrect answer when the timer ends
      } else {
        onNextQuestion(); // Trigger the next question when the timer reaches 0 and no answer is selected
      }
    }
  }, [timer]);

  const handleAnswer = (isCorrect: boolean) => {
    if (selectedOption !== null) {
      onAnswer(isCorrect);
    }
    onNextQuestion();
  };

  return (
    <div className="Question">
      <div className="ImageContainer">
        <img src={questionData.imageUrl} alt="Question" className="Image" />
      </div>
      <h2>{questionData.question}</h2>
      <div className="Options">
        {questionData.options.map((option, index) => (
          <button
            key={index}
            className={`Option ${
              selectedOption === index
                ? correctOption === index
                  ? 'Correct'
                  : 'Incorrect'
                : correctOption === index
                ? 'Correct'
                : ''
            }`}
            onClick={() => setSelectedOption(index)}
            disabled={timer <= 0}
          >
            {option}
          </button>
        ))}
      </div>
      <p>Time Remaining: {timer} seconds</p>
    </div>
  );
};

export default Question;
