import React, { useState, useEffect, useRef } from 'react';
import '../Quiz.css';

interface QuestionProps {
  questionData: {
    imageUrl: string;
    question: string;
    options: string[];
    answer: number;
    time: number;
  };
  onAnswer: (isCorrect: boolean) => void;
  onNextQuestion: () => void;
}

const Question: React.FC<QuestionProps> = ({
  questionData,
  onAnswer,
  onNextQuestion,
}) => {
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const correctOptionRef = useRef<number | null>(null);
  const timerRef = useRef<number>(questionData.time);

  useEffect(() => {
    setSelectedOption(null);
    timerRef.current = questionData.time;
    correctOptionRef.current = null;
  }, [questionData]);

  useEffect(() => {
    if (timerRef.current > 0) {
      const interval = setInterval(() => {
        timerRef.current -= 1;
      }, 1000);

      return () => clearInterval(interval);
    } else {
      if (selectedOption !== null) {
        const isCorrect = selectedOption === questionData.answer;
        onAnswer(isCorrect);
        correctOptionRef.current = isCorrect ? questionData.answer : selectedOption;
      } else {
        onAnswer(false);
        correctOptionRef.current = questionData.answer;
      }

      // Delay for 5 seconds before moving to the next question
      const delay = setTimeout(() => {
        onNextQuestion();
        clearTimeout(delay);
      }, 5000); // Delay for 5 seconds
    }
  }, [selectedOption]);

  const handleAnswer = (index: number) => {
    if (selectedOption === null && timerRef.current > 0) {
      setSelectedOption(index);
    }
  };

  const optionClass = (index: number) => {
    if (correctOptionRef.current !== null) {
      return correctOptionRef.current === index ? 'Correct' : 'Incorrect';
    }
    return selectedOption === index ? 'Selected' : '';
  };

  return (
    <div className="Question">
      <div className="ImageContainer">
        <img src={questionData.imageUrl} alt="Question" className="Image" />
      </div>
      <h2>{questionData.question}</h2>
      <div className="Options">
        {questionData.options.map((option, index) => (
          <button
            key={index}
            className={`Option ${optionClass(index)}`}
            onClick={() => handleAnswer(index)}
            disabled={timerRef.current <= 0}
          >
            {option}
          </button>
        ))}
      </div>
      <p>Time Remaining: {timerRef.current} seconds</p>
    </div>
  );
};

export default Question;
